mapper.select:
select * from ( select rownum as RNUM, A.* from ( select * from mailView where MAIL_TAKE = #{empNo} AND MAIL_TEMPSAVE IS NULL AND MAIL_RESERVE<=SYSDATE AND MAIL_DEL_CHECK='0'and MAIL_DEL_CHECK = '0'
order by MAIL_SENDDATE desc )A )
where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from
    (
       select * from mailView
       where MAIL_SEND = #{empNo} AND MAIL_TEMPSAVE IS NULL AND  MAIL_RESERVE<=SYSDATE  AND MAIL_DEL_CHECK='0'

       order by MAIL_SENDDATE desc 
    )A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from
    (
       select * from mailView
       where MAIL_SEND = #{empNo} AND MAIL_TEMPSAVE = 'T' AND  MAIL_RESERVE<=SYSDATE  AND MAIL_DEL_CHECK='0' 

       order by MAIL_SENDDATE desc 
    )A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, A.* from ( select * from mailView where MAIL_SEND = #{empNo} AND MAIL_RESERVE>=SYSDATE AND MAIL_TEMPSAVE IS NULL AND MAIL_DEL_CHECK='0' and MAIL_DEL_CHECK = '0'
order by MAIL_SENDDATE desc )A )
where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

	select *
	from
	(
	    select rownum as RNUM, A.*
	    from
	    (
		    select * 
		    from(
		        select * from mailview
     			where mail_take=#{empNo} and mail_del_check='D'

				union all
				select * from mailview
				where mail_send=#{empNo} and mail_del_check='D'

			)
			order by mail_senddate desc
	    )A
	)

	where RNUM > #{firstRecordIndex} 
	and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, A.* from ( select * from mailView where MAIL_TAKE = #{empNo} and MAIL_READDATE IS NULL AND MAIL_TEMPSAVE IS NULL AND MAIL_RESERVE<=SYSDATE AND MAIL_DEL_CHECK='0'
order by MAIL_SENDDATE desc )A )
where RNUM > #{firstRecordIndex} and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from
    (
       select * from mailView
       where MAIL_TAKE = #{empNo} AND MAIL_IMPORTANT = 'I' AND MAIL_TEMPSAVE IS NULL AND  MAIL_RESERVE<=SYSDATE  AND MAIL_DEL_CHECK='0'

       order by MAIL_SENDDATE desc 
    )A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update mail
set mail_del_check = 'D'
where mail_No = #{mailNo}

update mail
set MAIL_READDATE = SYSDATE
where MAIL_NO = #{mailNo}

update mail
set MAIL_READDATE = null
where MAIL_NO = #{mailNo}

update mail
set MAIL_IMPORTANT = 'I'
where MAIL_NO = #{mailNo}

update mail
set MAIL_IMPORTANT = '0'
where MAIL_NO = #{mailNo}
